settings.custom_liquid:
{% assign parts = product.title | split: '_' %}

<div class="page-width">
  {% if parts[0] == "[Pre-Order]" %}
    <h2>{{ parts[0] }} {{ parts[1] }}</h2>
    <h1>{{ parts[2] }}</h1>
  {% else %}
    <h2>{{ parts[0] }}</h2>
    <h1>{{ parts[1] }}</h1>
  {% endif %}
</div>

{%- assign collection = product.collections | first -%}
{%- assign pre_order_end_time2 = collection.metafields.custom.pre_order_end_time | date: '%Y-%m-%d' -%}
{%- assign pre_order_end_time = collection.metafields.custom.pre_order_end_time | date: '%s' | plus: 0 -%}
{%- assign current_time = 'now' | date: "%Y-%m-%d" -%} 
{%- assign estimated_arrival_time = collection.metafields.custom.estimated_time_of_arrival | date: '%Y-%m-%d' -%}

{%- if pre_order_end_time2 != blank -%} 
  <div class="pre-order-info-box" style="padding:0;margin:0;"> <p style="font-size: 18px;">
    {% if pre_order_end_time2 <= current_time %}
      {% if pre_order_end_time2 == current_time %}
        <span style="text-align: center;">Last Day to Pre-Order ({{ pre_order_end_time2 }})</span>
      {% else %}
        Pre-order closed on: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
      {% endif %}
      <br>
      Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
    {% else %}
      Pre-order deadline: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
      <br>
      Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
    {% endif %}
  </p></div>
{%- endif -%}

<style>
.pre-order-info-box {
  width: 100%; /* 設置寬度為100% */
  max-width: 1200px; /* 可選：設置最大寬度 */
  margin: 20px auto; /* 使用 auto 使其居中對齊 */
  border: 5px solid transparent;
  border-image: linear-gradient(45deg, #f2c0cb, #f88379, #ff69b4, #f2c0cb);
  border-image-slice: 1;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 5px;
  animation: borderAnimation 10s ; /* 延长动画时间并使用ease-in-out缓动效果 */
  border-radius: 20px; /* 圓角半徑 */
  background: #fff;
  overflow: hidden; /* 确保圆角边框效果 */
}

@keyframes borderAnimation {
  0%, 100% {
    border-image-source: linear-gradient(45deg, #f25ca2, #ff79c6, #e75480); /* 柔和粉紅、亮粉紅、桃紅 */
  }
  50% {
    border-image-source: linear-gradient(45deg, #89cff0, #a2d2ff); /* 淡藍、嬰兒藍 */
  }
}

</style>

{%- assign collection = product.collections | first -%}
{%- assign pre_order_end_time2 = collection.metafields.custom.pre_order_end_time | date: '%Y-%m-%d' -%}
{%- assign pre_order_end_time = collection.metafields.custom.pre_order_end_time | date: '%s' | plus: 0 -%}
{%- assign current_time = 'now' | date: "%Y-%m-%d" -%} 
{%- assign estimated_arrival_time = collection.metafields.custom.estimated_time_of_arrival | date: '%Y-%m-%d' -%}
{%- if pre_order_end_time2 != blank -%} 
  <div class="card">
    <a class="card1" href="#">
      <p>This is a Pre-Order Item</p>
      <p>
        {% if pre_order_end_time2 <= current_time %}
          {% if pre_order_end_time2 == current_time %}
            <span style="text-align: center;">Last Day to Pre-Order ({{ pre_order_end_time2 }})</span>
          {% else %}
            Pre-order closed on: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
          {% endif %}
            <br>
            Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
          {% else %}
            Pre-order deadline: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
            <br>
            Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
          {% endif %}
      </p>
      <div class="go-corner" href="https://babygliderph.com/pages/pre-order-terms-and-conditions">
        <div class="go-arrow">
          →
        </div>
      </div>
    </a>
  </div>
{%- endif -%}


{% style %}
.card p {
  font-size: 17px;
  font-weight: 400;
  line-height: 20px;
  color: #666;
}

.card p.small {
  font-size: 14px;
}

.go-corner {
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  width: 32px;
  height: 32px;
  overflow: hidden;
  top: 0;
  right: 0;
  background-color: #00838d;
  border-radius: 0 4px 0 32px;
}

.go-arrow {
  margin-top: -4px;
  margin-right: -4px;
  color: white;
  font-family: courier, sans;
}

.card1 {
  display: block;
  position: relative;
  max-width: 100%;
  background-color: #f2f8f9;
  border-radius: 4px;
  padding: 32px 24px;
  margin: 12px;
  text-decoration: none;
  z-index: 0;
  overflow: hidden;
}

.card1:before {
  content: "";
  position: absolute;
  z-index: -1;
  top: -16px;
  right: -16px;
  background: #00838d;
  height: 32px;
  width: 32px;
  border-radius: 32px;
  transform: scale(1);
  transform-origin: 50% 50%;
  transition: transform 0.25s ease-out;
}

.card1:hover:before {
  transform: scale(21);
}

.card1:hover p {
  transition: all 0.3s ease-out;
  color: rgba(255, 255, 255, 0.8);
}

.card1:hover h3 {
  transition: all 0.3s ease-out;
  color: #fff;
}

.card2 {
  display: block;
  top: 0px;
  position: relative;
  max-width: 262px;
  background-color: #f2f8f9;
  border-radius: 4px;
  padding: 32px 24px;
  margin: 12px;
  text-decoration: none;
  z-index: 0;
  overflow: hidden;
  border: 1px solid #f2f8f9;
}

.card2:hover {
  transition: all 0.2s ease-out;
  box-shadow: 0px 4px 8px rgba(38, 38, 38, 0.2);
  top: -4px;
  border: 1px solid #ccc;
  background-color: white;
}

.card2:before {
  content: "";
  position: absolute;
  z-index: -1;
  top: -16px;
  right: -16px;
  background: #00838d;
  height: 32px;
  width: 32px;
  border-radius: 32px;
  transform: scale(2);
  transform-origin: 50% 50%;
  transition: transform 0.15s ease-out;
}

.card2:hover:before {
  transform: scale(2.15);
}

.card3 {
  display: block;
  top: 0px;
  position: relative;
  max-width: 262px;
  background-color: #f2f8f9;
  border-radius: 4px;
  padding: 32px 24px;
  margin: 12px;
  text-decoration: none;
  overflow: hidden;
  border: 1px solid #f2f8f9;
}

.card3 .go-corner {
  opacity: 0.7;
}

.card3:hover {
  border: 1px solid #00838d;
  box-shadow: 0px 0px 999px 999px rgba(255, 255, 255, 0.5);
  z-index: 500;
}

.card3:hover p {
  color: #00838d;
}

.card3:hover .go-corner {
  transition: opactiy 0.3s linear;
  opacity: 1;
}

.card4 {
  display: block;
  top: 0px;
  position: relative;
  max-width: 262px;
  background-color: #fff;
  border-radius: 4px;
  padding: 32px 24px;
  margin: 12px;
  text-decoration: none;
  overflow: hidden;
  border: 1px solid #ccc;
}

.card4 .go-corner {
  background-color: #00838d;
  height: 100%;
  width: 16px;
  padding-right: 9px;
  border-radius: 0;
  transform: skew(6deg);
  margin-right: -36px;
  align-items: start;
  background-image: linear-gradient(-45deg, #8f479a 1%, #dc2a74 100%);
}

.card4 .go-arrow {
  transform: skew(-6deg);
  margin-left: -2px;
  margin-top: 9px;
  opacity: 0;
}

.card4:hover {
  border: 1px solid #cd3d73;
}

.card4 h3 {
  margin-top: 8px;
}

.card4:hover .go-corner {
  margin-right: -12px;
}

.card4:hover .go-arrow {
  opacity: 1;
}
{% endstyle %}
