settings.custom_liquid:
{% assign parts = product.title | split: '_' %}

<div class="page-width">
  {% if parts[0] == "[Pre-Order]" %}
    <h2>{{ parts[0] }} {{ parts[1] }}</h2>
    <h1>{{ parts[2] }}</h1>
  {% else %}
    <h2>{{ parts[0] }}</h2>
    <h1>{{ parts[1] }}</h1>
  {% endif %}
</div>

{%- assign collection = product.collections | first -%}
{%- assign pre_order_end_time2 = collection.metafields.custom.pre_order_end_time | date: '%Y-%m-%d' -%}
{%- assign pre_order_end_time = collection.metafields.custom.pre_order_end_time | date: '%s' | plus: 0 -%}
{%- assign current_time = 'now' | date: "%Y-%m-%d" -%} 
{%- assign estimated_arrival_time = collection.metafields.custom.estimated_time_of_arrival | date: '%Y-%m-%d' -%}

{%- if pre_order_end_time2 != blank -%} 
  <div class="pre-order-info-box"> <p style="font-size: 18px;">
    {% if pre_order_end_time2 <= current_time %}
      {% if pre_order_end_time2 == current_time %}
        <span style="text-align: center;">Last Day to Pre-Order ({{ pre_order_end_time2 }})</span>
      {% else %}
        Pre-order closed on: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
      {% endif %}
      <br>
      Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
    {% else %}
      Pre-order deadline: <span style="text-align: center;">{{ pre_order_end_time2 }}</span>
      <br>
      Estimated arrival: <span style="text-align: center;">{{ estimated_arrival_time }}</span>
    {% endif %}
  </p></div>
{%- endif -%}

<style>
.pre-order-info-box {
  width: 100%; /* 設置寬度為100% */
  max-width: 1200px; /* 可選：設置最大寬度 */
  margin: 20px auto; /* 使用 auto 使其居中對齊 */
  border: 5px solid transparent;
  border-image: linear-gradient(45deg, #f2c0cb, #f88379, #ff69b4, #f2c0cb);
  border-image-slice: 1;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 5px;
  animation: borderAnimation 10s ; /* 延长动画时间并使用ease-in-out缓动效果 */
  border-radius: 20px; /* 圓角半徑 */
  background: #fff;
  overflow: hidden; /* 确保圆角边框效果 */
}

@keyframes borderAnimation {
  0%, 100% {
    border-image-source: linear-gradient(45deg, #f25ca2, #ff79c6, #e75480); /* 柔和粉紅、亮粉紅、桃紅 */
  }
  50% {
    border-image-source: linear-gradient(45deg, #89cff0, #a2d2ff); /* 淡藍、嬰兒藍 */
  }
}

</style>
